import React, { Component } from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import drilldown from 'highcharts/modules/drilldown';






class ChartDisplayMain extends Component {

     constructor(props) {
        super(props);
        this.state = {
            dataDisplay:
        props.dataFile &&
        props.dataFile.map((data, index) => ({
          id: index,
          name: data.insid,
          x: data.dom,
          y: data.dow,
          drilldown: data.insid,
        })),
        options: {}
        }
      }

     componentDidUpdate(nextProps, prevState) {
         if (prevState !== this.state || nextProps !== this.props && this.state.dataDisplay.length) {
            console.log('2222 - dataList', this.state.dataDisplay)
            let options = {
                chart: {
                    type: 'column'
                  },
                  title: {
                    text: 'My chart'
                  },
                  series: [
                    {
                     // data: [[1, 3], [2, 5], [3, 7], [4, 1], [3.6, 5], [6, 4]]
                    //  data: [
                    //     {"dom":31,"dow":5,"insid":"XYZ_L3_S_H","hour":11,"SumHourly":2},{"dom":2,"dow":4,"insid":"XYZ_B2_N_H","hour":7,"SumHourly":2},{"dom":8,"dow":3,"insid":"XYZ_L1_M","hour":12,"SumHourly":12},{"dom":8,"dow":3,"insid":"XYZ_L14_SBH_M","hour":12,"SumHourly":4}
                    //  ]
                    data: this.state.dataDisplay
                    }
                  ]
            }

            this.setState({
                options: options
            })

        }
         }

    render() { 
    console.log('11111 - dataList', this.state)
        return ( 
            <div className='App' style={{width: '55%', border: '1px solid black'}}>
              <HighchartsReact highcharts={drilldown(Highcharts)} options={this.state.options} />  
            </div>
         );
    }
}

export default ChartDisplayMain;
