import React, { Component } from 'react';
import {connect} from 'react-redux';
import { Route, Redirect, Link, withRouter } from 'react-router-dom';
import DownloadLink from "react-download-link";
import { confirmAlert } from "react-confirm-alert";
import "react-confirm-alert/src/react-confirm-alert.css";

//const url = window.URL.createObjectURL(new Blob('/home/anisha/smartClean/public/front-end.json'));
// const link = document.createElement('a');
// link.href = url;
// link.setAttribute('download', 'file.pdf');
// document.body.appendChild(link);
// link.click();

const abc  = require('./front-end.json');

class List  extends Component {
    state = {  }

    getUserLocation = () => {

    }

    componentDidMount() {
        // confirmAlert({
        //   title: "Smart Clean App wants you to download a json file",
        //   message: "",
        //   buttons: [
        //     {
        //       label: "Yes",
        //       onClick: this.getUserLocation
        //     },
        //     {
        //       label: "No"
        //     }
        //   ]
        // });
        confirmAlert({
            customUI: ({ onClose }) => {
              return (
                <div style={{
                    width: '850px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    flexDirection: 'column',
                    padding: '45px',
                    borderRadius: 8,
                    backgroundColor: 'white',
                    boxShadow: "1px 4px 6px 1px #9E9E9E",
                    }}
                    >
                  <h1>Can you please download the JSON file?</h1>
                  <div style={{display: 'flex'}}>
                  <div 
                  style={{
                      display: 'flex',
                      marginRight: '55px', 
                      backgroundColor: '#DCDCDC', 
                      width: '300px',
                      alignItems: 'center',
                      justifyContent: 'center',
                      boxShadow: "1px 4px 6px 1px #9E9E9E",
                    }}
                  onClick={onClose}><p>No</p></div>
                  <div
                   style={{
                    display: 'flex',
                    backgroundColor: '#DCDCDC', 
                   width: '300px',
                   alignItems: 'center',
                   justifyContent: 'center',
                   boxShadow: "1px 4px 6px 1px #9E9E9E",
                }}
                  >
                        <DownloadLink
        label="Yes, Sure"
        filename="front-end.json"
        exportFile={() => Promise.resolve(JSON.stringify(abc)).then(onClose())}
    />
                  </div>
                  </div>
                </div>
              );
            }
          });
      }


    render() { 
        return ( <div
        style={{
            display: 'flex',
            alignItems: 'center',
            marginTop: '120px',
            justifyContent: 'center',
            flexDirection: 'column'
        }}
        >
            <div style={{display: 'flex', flexDirection: 'row'}}>
                <p style={{fontSize: 30}}>Hi, </p>
                <p style={{marginLeft: '20px', fontSize: 30}}>{this.props.UserDetails.userName}!</p>
            </div>


        </div> );
    }
}

const mapStateToProps = (state) => ({
    UserDetails: state.UserDetails
})

export default connect(mapStateToProps)(List);
