import React, { Component } from 'react';
import { Route, Redirect, Link, useHistory } from 'react-router-dom';
import List from './List';

class Login extends Component {
    state = { 
        userName: '',
        password: '',
        name: '',
        address: '',
        isAdmin: ''
     }

     onClickButton = () => {
         console.log('stored value: ', this.state);
       return <Link to='/List'></Link>
     }

    render() { 
        return (
            <div style={{
                display: 'flex',
                alignItems: 'center',
                marginTop: '100px',
                justifyContent: 'center',
                flexDirection: 'column'
            }}>
                <div style={{display: 'flex', flexDirection: 'row', justifyContent: 'space-between'}}>
                    <p style={{width: '175px', fontSize:24}}>User Name</p>
                    <input 
                    style={{fontSize: 24}}
                    value={this.state.userName} onChange={(e) => {
                        this.setState({
                        userName: e.target.value
                    })}} />
                </div>
                <div style={{display: 'flex', 
                flexDirection: 'row', 
                justifyContent: 'space-between',
                marginTop: '50px'
                }}>
                    <p style={{width: '125px', fontSize:24}}>Password</p>
                    <input 
                    style={{
                        width: '200px',
                        fontSize: 24
                    }}
                    type='password'  value={this.state.password} onChange={(e) => {
                        this.setState({
                            password: e.target.value
                    })}} />
                </div>
                <div style={{display: 'flex', 
                flexDirection: 'row', 
                justifyContent: 'space-between',
                marginTop: '50px'
                }}>
                    <p style={{width: '125px', fontSize:24}}>Name</p>
                    <input 
                    style={{
                        width: '200px',
                        fontSize: 24
                    }}
                    value={this.state.name} onChange={(e) => {
                        this.setState({
                            name: e.target.value
                    })}} />
                </div>
                <div style={{display: 'flex', 
                flexDirection: 'row', 
                justifyContent: 'space-between',
                marginTop: '50px'
                }}>
                    <p style={{width: '125px', marginLeft: '200px', fontSize:24}}>Address</p>
                    <input 
                    style={{
                        width: '400px',
                        fontSize: 24
                    }}
                    value={this.state.address} onChange={(e) => {
                        this.setState({
                            address: e.target.value
                    })}} />
                </div>
                <div style={{display: 'flex', 
                flexDirection: 'row', 
                justifyContent: 'space-between',
                marginTop: '50px'
                }}>
                    <p style={{width: '125px',
                    marginRight: '200px',
                    fontSize:24
                    }}>Are you the admin ?</p>
                    <input 
                    style={{
                        width: '50px',
                        height: '50px',
                        marginTop: '24px'
                    }}
                    type='radio'
                    checked={this.state.isAdmin}
                    value={this.state.name} 
                    onClick={() => {
                        this.setState({
                            isAdmin: !this.state.isAdmin
                    })}
                    } />
                </div>

           <div style={{
               marginTop: '35px', 
          // height: '60px', 
           width: '300px',
           backgroundColor: 'blue',
           alignItems: 'center',
           justifyContent: 'center'
           }}
           onClick={this.onClickButton}
           >
                <p style={{color: 'white', fontSize: 24, textAlign: 'center'}}>Create your account</p>
           </div>
            </div>
          );
    }
}

export default Login;
