import React, { Component } from 'react';
import {connect} from 'react-redux';
import { Route, Redirect, Link, withRouter } from 'react-router-dom';
import DownloadLink from "react-download-link";
import { confirmAlert } from "react-confirm-alert";
import "react-confirm-alert/src/react-confirm-alert.css";
import ChartDisplayMain from './ChartDisplayMain';

const abc  = require('./front-end.json');

class List  extends Component {
    state = {  }

    getUserLocation = () => {

    }

    componentDidMount() {
        confirmAlert({
            customUI: ({ onClose }) => {
              return (
                <div style={{
                    width: '850px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    flexDirection: 'column',
                    padding: '45px',
                    borderRadius: 8,
                    backgroundColor: 'white',
                    boxShadow: "1px 4px 6px 1px #9E9E9E",
                    }}
                    >
                  <h1>Can you please download the JSON file?</h1>
                  <div style={{display: 'flex'}}>
                  <div 
                  style={{
                      display: 'flex',
                      marginRight: '55px', 
                      backgroundColor: '#DCDCDC', 
                      width: '300px',
                      alignItems: 'center',
                      justifyContent: 'center',
                      boxShadow: "1px 4px 6px 1px #9E9E9E",
                    }}
                  onClick={() => {
                      onClose();
                      this.props.dispatch({
                        type: 'ADD_FILE',
                        data: {
                            dataFile: abc.slice(0, 30),
                           // dataFile: abc,
                        }
                 })

                      }}><p>No</p></div>
                  <div
                   style={{
                    display: 'flex',
                    backgroundColor: '#DCDCDC', 
                   width: '300px',
                   alignItems: 'center',
                   justifyContent: 'center',
                   boxShadow: "1px 4px 6px 1px #9E9E9E",
                }}
                  >
                        <DownloadLink
        label="Yes, Sure"
        filename="front-end.json"
        exportFile={() => Promise.resolve(JSON.stringify(abc)).then(onClose())}
    />
                  </div>
                  </div>
                </div>
              );
            }
          });
      }


    render() { 
        return ( 
        <div style={{display: 'flex', flexDirection: 'column'}}>
            <div style={{display: 'flex', backgroundColor: 'black', height: '105px'}}>
                <p style={{color: 'white', margin: '15px'}}>Types of Charts you want to see</p>
            </div>
        <div
        style={{
            display: 'flex',
            alignItems: 'center',
            marginTop: '80px',
            justifyContent: 'center',
            flexDirection: 'column'
        }}
        >
            <div style={{display: 'flex', flexDirection: 'row'}}>
                <p style={{fontSize: 40}}>Hi, </p>
                <p style={{marginLeft: '20px', fontSize: 40}}>{this.props.UserDetails.userName}!</p>
            </div>

        {this.props.dataFile ? (
        <ChartDisplayMain  dataFile={this.props.dataFile}/>) : null}
        </div> 
        </div>
        );
    }
}

const mapStateToProps = (state) => ({
    UserDetails: state.UserDetails,
    dataFile: state.dataFile.dataFile
})

export default connect(mapStateToProps)(List);
