const abc  = require('./front-end.json');

class List  extends Component {
    state = {  }

    getUserLocation = () => {
        return <DownloadLink
        label="Save "
        filename="front-end.json"
        exportFile={() => JSON.stringify(abc)}
    />
    }

    componentDidMount() {
        confirmAlert({
          title: "Smart Clean App wants you to download a json file",
          message: "",
          buttons: [
            {
              label: "Yes",
              onClick: this.getUserLocation
            },
            {
              label: "No"
            }
          ]
        });
      }


    render() { 
        return ( <div
        style={{
            display: 'flex',
            alignItems: 'center',
            marginTop: '220px',
            justifyContent: 'center',
            flexDirection: 'column'
        }}
        >
            <div style={{display: 'flex', flexDirection: 'row'}}>
                <p style={{fontSize: 30}}>Hi, </p>
                <p style={{marginLeft: '20px', fontSize: 30}}>{this.props.UserDetails.userName}!</p>
            </div>

<DownloadLink
    label="Save "
    filename="front-end.json"
    exportFile={() => JSON.stringify(abc)}
/>
        </div> );
    }
}

const mapStateToProps = (state) => ({
    UserDetails: state.UserDetails
})

export default connect(mapStateToProps)(List);
