import React, { Component } from 'react';
import {connect} from 'react-redux'
import { Route, Redirect, Link, withRouter } from 'react-router-dom';
import List from './List';

class Login extends Component {
    state = { 
        userName: '',
        password: '',
        name: '',
        phoneNumber: '',
        address: '',
        isAdmin: false,
        isError: true,
     }

     onClickButton = () => {
         const {userName, password, name, phoneNumber, address} = this.state;

         if ((userName != ''|| password != '' || name != '' || phoneNumber != '' || address != '') && !this.state.isError) {
            this.props.dispatch({
                type: 'ADD_TEST',
                data: {
                    userName: this.state.userName,
                    password: this.state.password,
                    name: this.state.name,
                    phoneNumber: this.state.phoneNumber,
                    address: this.state.address,
                    isAdmin: this.state.isAdmin
                }
         })

         this.props.history.push('List');
         } else {

            alert('please provide proper inputs!!!')
         }

     }


    render() { 
        console.log('1111', this.state.isError);
        return (
            <div style={{
                display: 'flex',
                alignItems: 'center',
                marginTop: '100px',
                justifyContent: 'center',
                flexDirection: 'column'
            }}>
                <div style={{display: 'flex', flexDirection: 'row', justifyContent: 'space-between'}}>
                    <p style={{width: '175px', fontSize:24}}>User Name</p>
                    <input 
                    style={{fontSize: 24}}
                    value={this.state.userName} onChange={(e) => {
                        this.setState({
                        userName: e.target.value
                    })}} />
                </div>




           <div style={{
               marginTop: '35px', 
          // height: '60px', 
           width: '300px',
           backgroundColor: 'blue',
           alignItems: 'center',
           justifyContent: 'center'
           }}
           onClick={this.onClickButton}
           >
                <p style={{color: 'white', fontSize: 24, textAlign: 'center'}}>Create your account</p>
           </div>
            </div>
          );
    }
}


const mapStateToProps = (state) => ({
    UserDetails: state.UserDetails
})

export default connect(mapStateToProps)(Login);

