import React, { Component } from 'react';
import {connect} from 'react-redux'
import { Route, Redirect, Link, withRouter } from 'react-router-dom';
import List from './List';

class Login extends Component {
    state = { 
        userName: '',
        password: '',
        name: '',
        phoneNumber: '',
        address: '',
        isAdmin: false,
        isError: true,
     }

     onClickButton = () => {
         //console.log('stored value: ', this.state);
         const {userName, password, name, phoneNumber, address} = this.state;

         if ((userName != ''|| password != '' || name != '' || phoneNumber != '' || address != '') && !this.state.isError) {
            this.props.dispatch({
                type: 'ADD_TEST',
                data: {
                    userName: this.state.userName,
                    password: this.state.password,
                    name: this.state.name,
                    phoneNumber: this.state.phoneNumber,
                    address: this.state.address,
                    isAdmin: this.state.isAdmin
                }
         })

         this.props.history.push('List');
         } else {

            alert('please provide proper phone number')
         }

     }


    render() { 
        console.log('1111', this.state.isError);
        return (
            <div style={{
                display: 'flex',
                alignItems: 'center',
                marginTop: '100px',
                justifyContent: 'center',
                flexDirection: 'column'
            }}>
                <div style={{display: 'flex', flexDirection: 'row', justifyContent: 'space-between'}}>
                    <p style={{width: '175px', fontSize:24}}>User Name</p>
                    <input 
                    style={{fontSize: 24}}
                    value={this.state.userName} onChange={(e) => {
                        this.setState({
                        userName: e.target.value
                    })}} />
                </div>
                <div style={{display: 'flex', 
                flexDirection: 'row', 
                justifyContent: 'space-between',
                marginTop: '50px'
                }}>
                    <p style={{width: '125px', fontSize:24}}>Password</p>
                    <input 
                    style={{
                        width: '200px',
                        fontSize: 24
                    }}
                    type='password'  value={this.state.password} onChange={(e) => {
                        this.setState({
                            password: e.target.value
                    })}} />
                </div>
                <div style={{display: 'flex', 
                flexDirection: 'row', 
                justifyContent: 'space-between',
                marginTop: '50px'
                }}>
                    <p style={{width: '125px', fontSize:24}}>Name</p>
                    <input 
                    style={{
                        width: '200px',
                        fontSize: 24
                    }}
                    value={this.state.name} onChange={(e) => {
                        this.setState({
                            name: e.target.value
                    })}} />
                </div>
                <div style={{display: 'flex', 
                flexDirection: 'row', 
                justifyContent: 'space-between',
                marginTop: '50px'
                }}>
                    <p style={{width: '175px', marginLeft: '200px', fontSize:24}}>Address</p>
                    <input 
                    style={{
                        width: '400px',
                        fontSize: 24
                    }}
                    value={this.state.address} onChange={(e) => {
                        this.setState({
                            address: e.target.value
                    })}} />
                </div>
                <div style={{display: 'flex', 
                flexDirection: 'row', 
                justifyContent: 'space-between',
                marginTop: '50px'
                }}>
                    <p style={{width: '125px', marginLeft: '200px', fontSize:24}}>Phone</p>
                    <input 
                    style={{
                        width: '200px',
                        fontSize: 24,
                        borderColor: this.state.isError && this.state.phoneNumber ? 'red' : 'black'
                    }}
                    value={this.state.phoneNumber} onChange={(e) => {
                       //let hash = '22222';
                       this.setState({
                        phoneNumber: e.target.value
                })
                        var reg = new RegExp('[0-9]$');
                        if (!reg || e.target.value.length != 10) {
                            console.log(reg.test(e.target.value));
                            this.setState({
                                isError:true
                        })
                        } else {
                            this.setState({
                                isError:false
                        })
                        }
                       //console.log(reg.test(e.target.value));

                    }} />
                </div>
                <div style={{display: 'flex', 
                flexDirection: 'row', 
                justifyContent: 'space-between',
                marginTop: '50px'
                }}>
                    <p style={{width: '125px',
                    marginRight: '200px',
                    fontSize:24
                    }}>Are you the admin ?</p>
                    <input 
                    style={{
                        width: '50px',
                        height: '50px',
                        marginTop: '24px'
                    }}
                    type='radio'
                    checked={this.state.isAdmin}
                    value={this.state.name} 
                    onClick={() => {
                        this.setState({
                            isAdmin: !this.state.isAdmin
                    })}
                    } />
                </div>

           <div style={{
               marginTop: '35px', 
          // height: '60px', 
           width: '300px',
           backgroundColor: 'blue',
           alignItems: 'center',
           justifyContent: 'center'
           }}
           onClick={this.onClickButton}
           >
                <p style={{color: 'white', fontSize: 24, textAlign: 'center'}}>Create your account</p>
           </div>
            </div>
          );
    }
}


const mapStateToProps = (state) => ({
    UserDetails: state.UserDetails
})

export default connect(mapStateToProps)(Login);

//export default Login;
