import React, { Component } from 'react';
import {connect} from 'react-redux';
import { Route, Redirect, Link, withRouter } from 'react-router-dom';
import DownloadLink from "react-download-link";
import { confirmAlert } from "react-confirm-alert";
import "react-confirm-alert/src/react-confirm-alert.css";

//const url = window.URL.createObjectURL(new Blob('/home/anisha/smartClean/public/front-end.json'));
// const link = document.createElement('a');
// link.href = url;
// link.setAttribute('download', 'file.pdf');
// document.body.appendChild(link);
// link.click();

const abc  = require('./front-end.json');

class List  extends Component {
    state = {  }

    getUserLocation = () => {

    }

    componentDidMount() {
        // confirmAlert({
        //   title: "Smart Clean App wants you to download a json file",
        //   message: "",
        //   buttons: [
        //     {
        //       label: "Yes",
        //       onClick: this.getUserLocation
        //     },
        //     {
        //       label: "No"
        //     }
        //   ]
        // });
        confirmAlert({
            customUI: ({ onClose }) => {
              return (
                <div style={{
                    width: '850px', 
                    border: '1px solid black',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    flexDirection: 'column'
                    }}
                    >
                  <h1>Can you please download the JSON file?</h1>
                  <button onClick={onClose}>No</button>
                  <button
                    onClick={() => {
                      this.getUserLocation();
                      onClose();
                    }}
                  >
                    {/* Yes, Download it! */}
                    <DownloadLink
    label="Yes, Download it "
    filename="front-end.json"
    exportFile={() => JSON.stringify(abc)}
/>
                  </button>
                </div>
              );
            }
          });
      }


    render() { 
        return ( <div
        style={{
            display: 'flex',
            alignItems: 'center',
            marginTop: '220px',
            justifyContent: 'center',
            flexDirection: 'column'
        }}
        >
            <div style={{display: 'flex', flexDirection: 'row'}}>
                <p style={{fontSize: 30}}>Hi, </p>
                <p style={{marginLeft: '20px', fontSize: 30}}>{this.props.UserDetails.userName}!</p>
            </div>


        </div> );
    }
}

const mapStateToProps = (state) => ({
    UserDetails: state.UserDetails
})

export default connect(mapStateToProps)(List);
